$(window).on("load", function () {
    var a = $("#bar-chart");
    new Chart(a, {
        type: "horizontalBar",
        options: {
            elements: { rectangle: { borderWidth: 2, borderColor: "rgb(0, 255, 0)", borderSkipped: "left" } },
            responsive: !0,
            maintainAspectRatio: !1,
            responsiveAnimationDuration: 500,
            legend: { position: "bottom" },
            scales: { xAxes: [{ display: !0, gridLines: { color: "#f3f3f3", drawTicks: !1 }, scaleLabel: { display: !0 } }], yAxes: [{ display: !0, gridLines: { color: "#f3f3f3", drawTicks: !1 }, scaleLabel: { display: !0 } }] },
            title: { display: !1, text: "Chart.js Horizontal Bar Chart" },
        },
        data: {
            labels: ["Pengepul", "Bank Sampah Induk", "Bisnis", "Hotel", "Sekolah", "TPA", "EB Residential Service", "Bank Sampah Unit"],
            datasets: [
                { label: "Category", data: [65, 59, 42, 81, 20, 5,20,76], backgroundColor: "#28D094", hoverBackgroundColor: "rgba(22,211,154,.9)", borderColor: "transparent" },
            ],
        },
    });
});
