function drawBar() {
    var e = google.visualization.arrayToDataTable([
        ["Category", 'Contribution', { role: 'style' }],
        ["Bank Sampah Unit", 880, '#c6ebc9'],
        ["Bisnis", 730, '#70af85'],
        ["Hotel", 960, '#f0e2d0'],
        ["Pengepul", 1340, '#aa8976'],
        ["TPST3R", 1160, '#125d98'],
    ]);
    new google.visualization.BarChart(document.getElementById("bar-chart")).draw(e, {
        height: 400,
        fontSize: 12,
        chartArea: { left: "5%", width: "80%", height: "80%" },
        hAxis: { gridlines: { color: "#e9e9e9" } },
        vAxis: { gridlines: { count: 10 }, minValue: 0 },
        legend: { position: "none" },
    });
}
google.load("visualization", "1.0", { packages: ["corechart"] }),
    google.setOnLoadCallback(drawBar),
    $(function () {
        function e() {
            drawBar();
        }
        $(window).on("resize", e), $(".menu-toggle").on("click", e);
    });
