function drawLine() {
    var e = google.visualization.arrayToDataTable([
        ["Year", "Sales", "Costs","aa"],
        ["2010", 880, 250,76],
        ["2011", 530, 350,88],
        ["2012", 425, 999,90],
        ["2013", 750, 350,222],
        ["2014", 550, 780,111],
        ["2015", 880, 300,900],
    ]);
    new google.visualization.LineChart(document.getElementById("line-chart")).draw(e, {
        height: 500,
        fontSize: 12,
        curveType: "function",
        colors: ["#37BC9B", "#DA4453","#DA4153","#DA8453","#DF4453"],
        pointSize: 5,
        chartArea: { width: "90%", height: "90%" },
        vAxis: { gridlines: { color: "#e9e9e9", count: 10 }, minValue: 0 },
        legend: { position: "top", alignment: "center", textStyle: { fontSize: 12 } },
    });
}
google.load("visualization", "1.0", { packages: ["corechart"] }),
    google.setOnLoadCallback(drawLine),
    $(function () {
        function e() {
            drawLine();
        }
        $(window).on("resize", e), $(".menu-toggle").on("click", e);
    });
